mapper.select:



        AND line_user_id like concat('%', #{lineUserId}, '%')


        AND line_display_name like concat('%', #{lineDisplayName}, '%')


        AND sys_user_id = #{sysUserId}


        AND bind_status = #{bindStatus.code}


        AND follow_status = #{followStatus.code}


        and date_format(first_follow_time,'%Y-%m-%d') >= date_format(#{params.beginTime},'%Y-%m-%d')


        and date_format(first_follow_time,'%Y-%m-%d') <= date_format(#{params.endTime},'%Y-%m-%d')


order by create_time desc

mapper.update:
update line_user
line_display_name = #{lineDisplayName}, line_picture_url = #{linePictureUrl}, line_status_message = #{lineStatusMessage}, line_language = #{lineLanguage}, sys_user_id = #{sysUserId}, bind_status = #{bindStatus}, follow_status = #{followStatus}, latest_follow_time = #{latestFollowTime}, unfollow_time = #{unfollowTime}, block_time = #{blockTime}, latest_bind_time = #{latestBindTime}, unbind_time = #{unbindTime}, bind_count = #{bindCount}, total_messages_sent = #{totalMessagesSent}, total_messages_received = #{totalMessagesReceived}, last_interaction_time = #{lastInteractionTime}, remark = #{remark}, update_time = sysdate()
where id = #{id}
