mapper.insert:
INSERT INTO shop_cvs_store_temp (
    store_key, member_id, logistics_sub, store_id, store_name,
    store_address, store_tel, cvs_outside, expire_time, create_time
) VALUES (
    #{storeKey}, #{memberId}, #{logisticsSub}, #{storeId}, #{storeName},
    #{storeAddress}, #{storeTel}, #{cvsOutside}, #{expireTime}, #{createTime}
)

mapper.select:
SELECT 
FROM shop_cvs_store_temp
WHERE store_key = #{storeKey}
  AND member_id = #{memberId}
