mapper.select:

where b.status = '1' and b.expected_return < now()

    and b.borrower_id = #{borrowerId}

order by b.expected_return asc

mapper.update:
update inv_borrow
borrow_no = #{borrowNo}, return_quantity = #{returnQuantity}, item_id = #{itemId}, quantity = #{quantity}, borrower_id = #{borrowerId}, borrower_name = #{borrowerName}, borrow_time = #{borrowTime}, expected_return = #{expectedReturn}, actual_return = #{actualReturn}, purpose = #{purpose}, status = #{status}, approver_id = #{approverId}, approver_name = #{approverName}, approve_time = #{approveTime}, approve_remark = #{approveRemark}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, reserve_status = #{reserveStatus}, reserve_start_date = #{reserveStartDate}, reserve_end_date = #{reserveEndDate},
where borrow_id = #{borrowId}

mapper.select:

where b.item_id = #{itemId}
  and b.status in ('0', '1', '4', '5')
order by b.borrow_time desc


where b.borrower_id = #{userId}
  and b.item_id = #{itemId}
  and b.reserve_status = 1  
  and b.status = '0'  
order by b.create_time desc
